mapper.select:
SELECT 

FROM coupon_code a

WHERE a.id = #{id}

SELECT
FROM coupon_code a LEFT JOIN coupon b ON b.id = a.coupon_id LEFT JOIN mem_member m ON m.id = a.member_id
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.coupon_id = #{coupon.id}
ORDER BY ${page.orderBy}
ORDER BY a.create_date ASC

SELECT 

FROM coupon_code a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.coupon_id = #{coupon.id}




		ORDER BY ${page.orderBy}


		ORDER BY a.create_date ASC

SELECT 
	count(*)
FROM coupon_code a
WHERE a.del_flag = 0 AND a.coupon_id = #{id}

		AND a.is_used = #{isUsed}

mapper.update:
UPDATE coupon_code SET 
	del_flag = #{DEL_FLAG_DELETE}


		WHERE id = #{id}


		WHERE coupon_id = #{coupon.id}

mapper.select:
SELECT 

FROM coupon_code a
WHERE del_flag = #{DEL_FLAG_NORMAL} AND a.id = #{id}
