mapper.select:
SELECT ,
FROM mem_member a LEFT JOIN mem_rank b ON a.rank_id = b.id
WHERE a.id = #{id}

SELECT 
	,

FROM mem_member a 
LEFT JOIN mem_rank b ON a.rank_id = b.id


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')



		AND a.id = #{id}



		AND a.approve_flag = #{approveFlag}



		AND a.user_type = #{userType}


		AND a.phone = #{phone}



			AND b.name = #{memRank.name}




		AND a.create_date >= #{starDate}


		AND a.create_date <= #{endDate}


		AND a.is_locked = #{isLocked}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT a.id AS "id", a.username AS "username", a.phone AS "phone", a.amount AS "amount", a.rank_id AS "memRank.id", a.id_card AS "idCard", a.approve_flag AS "approveFlag", a.is_locked AS "isLocked", a.remarks AS "remarks", a.user_type AS "userType", b.id AS "bid", b.name AS "bname", a.sumamount AS "sumamount" FROM mem_member a LEFT JOIN mem_rank b ON a.rank_id = b.id
a.del_flag = #{DEL_FLAG_NORMAL} and a.approve_flag in ('0','1','2')
AND a.username LIKE '%'||#{username}||'%' '%'+#{username}+'%' concat('%',#{username},'%')
AND a.approve_flag = #{approveFlag}
AND a.user_type = #{userType}
AND a.phone = #{phone}
AND b.name = #{memRank.name}
AND a.is_locked = #{isLocked}
ORDER BY a.update_date DESC
ORDER BY a.update_date DESC

SELECT 

FROM mem_member a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE mem_member SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

