mapper.select:
SELECT 

FROM order_table a

WHERE a.id = #{id}

SELECT 

FROM order_table a



		AND a.order_no = #{orderNo}


		AND a.order_source = #{orderSource}


		AND a.order_status = #{orderStatus}







		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT a.id , a.order_no AS "orderNo", a.address AS "address", a.amount_paid AS "amountPaid", a.area_name AS "areaName", a.consignee AS "consignee", a.coupon_discount AS "couponDiscount", a.expire AS "expire", a.fee AS "fee", a.freight AS "freight", a.operator AS "operator", a.invoice_title AS "invoiceTitle", a.is_allocated_stock AS "isAllocatedStock", a.is_invoice AS "isInvoice", a.lock_expire AS "lockExpire", a.memo AS "memo", a.member_no AS "memMember.id", a.offset_amount AS "offsetAmount", a.order_status AS "orderStatus", a.payment_method_name AS "paymentMethodName", a.payment_status AS "paymentStatus", a.phone AS "phone", a.point AS "point", a.promotion AS "promotion", a.promotion_discount AS "promotionDiscount", a.shipping_method_name AS "shippingMethodName", a.shipping_status AS "shippingStatus", a.tax AS "tax", a.exception_remarks AS "exceptionRemarks", a.is_remarks AS "isRemarks", a.zip_code AS "zipCode", a.area AS "area", a.coupon_code AS "couponCode.id", a.payment_method AS "paymentMethod.id", a.shipping_method AS "shippingMethod.id", m.username AS "userName", a.distributor_name AS "distributorName", a.distributor_phone AS "distributorPhone", a.order_source AS "orderSource", a.shipping_date AS "shippingDate", a.create_by AS "createBy.id", a.create_date AS "createDate", a.update_by AS "updateBy.id", a.update_date AS "updateDate", a.remarks AS "remarks", a.del_flag AS "delFlag", s.company_name AS "supplierName", p.shipping_no AS "shippingNo", p.tracking_no AS "trackingNo" FROM order_table AS a LEFT JOIN mem_member m on a.member_no=m.id LEFT JOIN supplier s ON a.`supplier_id` = s.`id` LEFT JOIN order_table_shipping P ON a.order_no = p.order_no
AND a.order_no = #{orderNo}
AND a.order_source = #{orderSource}
AND a.order_status = #{orderStatus}
AND a.supplier_id = #{supplierId}
AND a.user_name LIKE '%'||#{userName}||'%' '%'+#{userName}+'%' concat('%',#{userName},'%')
AND a.order_no = (select order_no from order_table_shipping where tracking_no=#{trackingNo})
AND a.phone = #{phone}
AND a.create_date >= #{beginDate}
AND a.create_date <= #{endDate}
ORDER BY a.create_date desc

SELECT 

FROM order_table a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.order_source = #{orderSource}


		AND a.order_status = #{orderStatus}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE order_table SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
