mapper.select:
SELECT 

FROM order_table_item a

WHERE a.id = #{id}

SELECT 

FROM order_table_item a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.order_table_id = #{orderTable.id}


		AND a.is_gift = #{isGift}


		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.goods_no = #{product.id}


		AND a.order_no = #{orderNo}




		ORDER BY ${page.orderBy}


		ORDER BY a.create_date ASC

SELECT 

FROM order_table_item a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.create_date ASC

mapper.update:
UPDATE order_table_item SET 
	del_flag = #{DEL_FLAG_DELETE}


		WHERE id = #{id}


		WHERE order_table_id = #{orderTable.id}

UPDATE order_table_item SET 	
	return_status = #{returnStatus}
WHERE 
    goods_no=#{product.id}
    and order_no=#{orderNo}
    and order_type=#{productType}

		AND recommended_id = #{reMemberIdString}
