mapper.select:
SELECT 

FROM order_table_shipping_item a

WHERE a.id = #{id}

SELECT 

FROM order_table_shipping_item a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.order_table_shipping_id = #{orderTableShipping.id}


		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')




		ORDER BY ${page.orderBy}


		ORDER BY a.create_date ASC

SELECT 

FROM order_table_shipping_item a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.create_date ASC

SELECT

FROM order_table_shipping_item a
WHERE a.order_table_shipping_id = #{shippingId}

mapper.update:
UPDATE order_table_shipping_item SET 
	del_flag = #{DEL_FLAG_DELETE}


		WHERE id = #{id}


		WHERE order_table_shipping_id = #{orderTableShipping.id}
